package practicejava;

public class printingways {
    
	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int x = 100;
      char name = 'D';
      System.out.print("hello");
      System.out.println("world");
      System.out.println("hi this is divya");
      System.out.println("welcome to my java world");
      System.out.printf("my age is %d%n",21 );
      System.out.println(7);
      System.out.printf("%c%n",'A'); // printing character
      System.out.println('A');
      System.out.println(x);// printing a variable 
      System.out.printf("%c",name);
      System.out.printf("%d, %c, %s%n",21,'d',"divya");
      System.out.print("number " + x);
      
	}

}
